Bash(AUTH_METHOD=windows-integrated SERVER_NAME=bbi-production DATABASE_NAME=Docupoint_BB TRUST_SERVER_CERTIFICATE=true READONLY=true node -e "
const { createSqlConfig } = await import\(''file:///c:/Repos/SQL-AI-samples/MssqlMcp/Node/dist/index.js''\);
const config = await createSqlConfig\(\);
console.log\(''Config OK:'', config.config.connectionString\);
const sqlMod = await import\(''mssql/msnodesqlv8.js''\);
const pool = await sqlMod.default.connect\(config.config\);
const res = await pool.request\(\).query\(''SELECT TOP 3 TABLE_SCHEMA, TABLE_NAME FROM INFORMATION_SCHEMA.TABLES ORDER BY TABLE_NAME''\);
console.log\(''Tables:'', JSON.stringify\(res.recordset, null, 2\)\);
await pool.close\(\);
process.exit\(0\);
")

